import React, { useState, useEffect } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import {
  BrowserRouter as Router,
  Route,
  Switch,
} from 'react-router-dom';
import NavBar from './pages/NavBar/NavBar'

import HomePage from './pages/HomePage'
import LoginPage from './pages/LoginPage'
import SignUpPage from './pages/SignUpPage'
import LogOutPage from './pages/LogOutPage'
import ProfilePage from './pages/ProfilePage'
import NotFoundPage from './pages/NotFoundPage'

import RouteHandler from './router/RouteHandler'

// import LogOutAction from './authens/authens'

function App() {
  const [loggedIn, setLoggedIn] = useState(localStorage.getItem("myToken"));
  // const loggedIn = localStorage.getItem("myToken")

  return (
    <Router>
      <div className="App">
        <div className="NavBar">
          <NavBar isAuth={loggedIn} />
        </div>
        <div className="auth-wrapper">
            <div className="auth-inner">
              <Switch>
                {loggedIn ? 
                  (<>
                    <Route exact path="/" component={HomePage} />                
                    <Route path="/logout" render={() => <LogOutPage setLoggedIn={setLoggedIn} />} />
                    <RouteHandler loggedIn={loggedIn} path='/profile' component={ProfilePage} />
                  </>) 
                  : 
                  (<>
                    <Route exact path="/" component={HomePage} />
                    <Route path="/login" render={() => <LoginPage setLoggedIn={setLoggedIn} />} />
                    <Route path="/signup" render={() => <SignUpPage setLoggedIn={setLoggedIn} />} />      
                  </>)
                }
                <Route path='*' component={NotFoundPage} />
            </Switch>
          </div>
        </div>
      </div>
    </Router>
  );
}

export default App;
